mapper.select:
SELECT * 
FROM orderhistory 
WHERE member_Idx = #{sIdx} 
ORDER BY orderdate DESC, idx DESC LIMIT #{pageVO.startIndexNo}, #{pageVO.pageSize};

SELECT ohd.idx, ohd.orderhistory_idx, ohd.review_Status ,ohd.complainStatus, oh.shipping_num, oh.orderdate, ohd.status, ohd.goods_idx, ohd.option_idx, ohd.goods_stock, g.thumbnail AS goods_thumbnail, g.name AS goods_name, go.goods_option, b.name AS goods_brand, g.price AS goods_Price FROM orderhistory oh JOIN orderhistory_detail ohd ON ohd.orderhistory_idx = oh.idx JOIN goods g ON g.idx = ohd.goods_idx JOIN goods_option go ON go.option_idx = ohd.option_idx JOIN brand b ON b.idx = g.brand_idx WHERE oh.member_idx = #{sIdx}
AND status = '결제완료' AND status = '배송중' AND status = '구매확정' AND complainStatus = '교환상품' AND complainStatus = '환불상품'
ORDER BY ohd.idx DESC, idx DESC LIMIT #{pageVO.startIndexNo}, #{pageVO.pageSize};

SELECT count(*) FROM orderhistory oh JOIN orderhistory_detail ohd ON ohd.orderhistory_idx = oh.idx WHERE oh.member_idx = #{sIdx}
AND status = '결제완료' AND status = '배송중' AND status = '구매확정' AND complainStatus = '교환상품' AND complainStatus = '환불상품'
;

SELECT count(*)
FROM orderhistory
WHERE member_idx = #{sIdx};

