mapper.insert:
INSERT INTO goods_stock (goods_idx, option_idx, stock) VALUES
((SELECT idx FROM goods ORDER BY idx DESC LIMIT 0, 1), (SELECT option_idx FROM goods_option WHERE goods_option = #{key}), #{value})
;

